//overlay
!(function() {
  var overlay_resize;

  window.toggle_overlay = function() {
    var content;
    content = $('.overlay-content');
    if (content.hasClass('overlayShowing')) {
      $('.overlay').hide();
      content.css('opacity', 0);
      $('.overlay-bg').fadeTo('opacity', 0);
    } else {
      overlay_resize();
      $('.overlay').show();
      content.fadeTo("fast", 1);
      $('.overlay-bg').fadeTo("fast", 1);
    }
    content.toggleClass('overlayShowing');
    //return $(".btn").button('reset');
  };

  overlay_resize = function() {
    var maskHeight, maskWidth;
    maskHeight = $(document).height();
    maskWidth = $(window).width();
    return $('.overlay-bg').css({
      'width': maskWidth,
      'height': maskHeight
    });
  };

  $('.overlay-content .destroy').click(function(){
    return toggle_overlay();
    console.log('oh hayyyy');
  });

  $('.overlay-bg').click(function(){
    return toggle_overlay();
  });

  $(window).resize(function() {
    return overlay_resize();
  });

}).call(this);
// end overlay